write a function to find the sum of all integers between any two values

sum_between <- function(a, b) {
  total_sum <- 0

  if (a < b) {
    start_val <- a
    end_val <- b
  } else {
    start_val <- b
    end_val <- a
  }

  for (i in start_val:end_val) {
    total_sum <- total_sum + i
  }

  return(total_sum)
}

sum1 <- sum_between(1, 10)
print(sum1)  # Output: 55

